func main(){
	EXPECT("1", 1, 1)
	EXPECT("1+1", 1+1, 2)
	EXPECT("1+2+3", 1+2+3, 6)
	EXPECT("3-2", 3-2, 1)
	EXPECT("3*2", 3*2, 6)
	EXPECT("6/2", 6/2, 3)
	EXPECT("3+2*3", 3+2*3, 9)
	EXPECT("8%3", 8%3, 2)
	EXPECT("5*(2+3)", 5*(2+3), 25)
	EXPECT("3 + +3", 3+ +3, 6)
	EXPECT("3 + -3", 3+ -3, 0)
	EXPECT("7&4", 7&4, 4)
	EXPECT("8|1", 8|1, 9)
	EXPECT("1 == 1", 1 == 1, 1)
	EXPECT("1 == 0", 1 == 0, 0)
	EXPECT("1 != 0", 1 != 0, 1)
	EXPECT("1 != 1", 1 != 1, 0)
	EXPECT("2>1", 2>1, 1)
	EXPECT("1<2", 1<2, 1)
	EXPECT("2 < 3 || 3 > 2", 2 < 3 || 3 > 2, 1)
	EXPECT("3 < 2 || 3 < 2", 3 < 2 || 3 < 2, 0)
	EXPECT("5 > 3 && 3 > 2", 5 > 3 && 3 > 2, 1)
	EXPECT("5 > 3 && 2 > 5", 5 > 3 && 2 > 5, 0)
	EXPECT("2 >= 2", 2 >= 2, 1)
	EXPECT("2 <= 2", 2 <= 2, 1)
	EXPECT("8 >> 2", 8 >> 2, 2)
	EXPECT("1 << 3", 1 << 3, 8)
	EXPECT("13 ^ 6", 13 ^ 6, 11)
	EXPECT("13 &^ 6", 13 &^ 6, 9)
}
